import React, { useState, useEffect } from 'react';
import './Artists.css';
import Artisttem from './ArtistItem';
import TrackItem from './TrackItem';
import axios from 'axios'

function Recommendations() {
  let genres_count = {};
  let count = 1;

  useEffect(() => {
    axios.get('http://localhost:5000/artists').then(response => {
      response.data.items.map(artist => (
          artist.genres.map(genre => (
            genres_count[genre] = (genres_count[genre] || 0) + 1
          ))
      ))
    });

    var key_values = []
    for (var key in key_values) {
        key_values.push([ key, object[key] ])
    }

    key_values.sort(function compare(kv1, kv2) {
        // This comparison function has 3 return cases:
        // - Negative number: kv1 should be placed BEFORE kv2
        // - Positive number: kv1 should be placed AFTER kv2
        // - Zero: they are equal, any order is ok between these 2 items
        return kv1[1] - kv2[1]
    })
    console.log(key_values)
  }, [])


  return (
    <div className='cards'>
      <h1>Recommendations</h1>
      <div className='cards__container'>
        <div className='cards__wrapper'>
          <ul className='cards__items'>
            {
              // map TrackItem here after generating the recommendeded tracks
            }
          </ul>
        </div>
      </div>
    </div>
  );

}

export default Recommendations;
