import React, { useState, useEffect } from 'react';
import './Artists.css';
import TrackItem from './TrackItem';
import axios from 'axios'

function Recommendations() {
  const [artists, setArtists] = useState([]);
  const [genres, setGenres] = useState([]);
  var genres_count = {};

  useEffect(() => {
    axios.get('http://localhost:5000/artists').then(response => {
      setArtists(response.data.items);
      console.log(response.data.items);
      for(var artist in response.data.items) {
        for(var genre in artist.genres) {
            console.log(genre);
            genres_count[genre] = (genres_count[genre] || 0) + 1;
        }    
    }
    });

  
    

    console.log(genres_count);

  }, [])


  return (
    <div className='cards'>
      <h1>Recommendations</h1>
      <div className='cards__container'>
        <div className='cards__wrapper'>
          <ul className='cards__items'>
            {
              genres === 0 ? (
                <p>Fetching your recent tracks!</p>
              ) : (
                  genres.map(genre => (
                    <TrackItem
                        src={genre.album.images[0].url}
                        text={genre.name}
                        popularity={genre.popularity}
                        duration={genre.duration_ms}
                        path={genre.external_urls.spotify}
                        />
                    )))
            }
          </ul>
        </div>
      </div>
    </div>
  );

}

export default Recommendations;
