import React from "react";
import axios from 'axios';
import './Popup.css';


let theList = [];
let type = "";

function Popup(props) {
  theList = props.theList;
  type = props.type;

  return (
    <div className="popup-box">
      <div className="box">
        <span className="close-icon" onClick={props.handleClose}>x</span>
        {props.content}
        <form>
          <div className="row">
            <div className="col-25">
              <label for="fname">Name</label>
            </div>
            <div className="col-75">
              <input type="text" id="fname" name="firstname" placeholder="Your name.."></input>
            </div>
          </div>
          <div class="row">
            <div className="col-25">
              <label for="fname">Description</label>
            </div>
            <div className="col-75">
              <input type="text" id="fname" name="firstname" placeholder="Your name.."></input>
            </div>
          </div>
          <div class="row">
            <div class="col-25">
              <label for="country">Country</label>
            </div>
            <div class="col-75">
              <select id="country" name="country">
                <option value="australia">Australia</option>
                <option value="canada">Canada</option>
                <option value="usa">USA</option>
              </select>
            </div>
          </div>
          <br/>
          <div class="row" margin="auto">
            <input type="submit" value="Submit" />
          </div>
        </form>
      </div>
    </div>
  );
};

const getFormData = () => {
  let name = document.getElementById('name').value;
  let description = document.getElementById('description').value;
  let privacy = document.getElementById('privacy').value;

  document.getElementById('submit-playlist').disabled = 'disabled';

  axios("http://localhost:5000/createPlaylist", {
    method: 'post',
    data: {
      name: name,
      description: description,
      privacy: privacy,
      arr: theList,
      type: type
    },
    withCredentials: true
  })
    .then((response) => {
      window.location.href = "http://localhost:3000/showplaylists";
    })

  //axios.get("http://localhost:5000/showplaylists");
}

export default Popup;
