import React, { useState } from 'react';
import { RecoMenuItems } from './RecoMenuItems';
import './RecoDropdown.css';
import { Link } from 'react-router-dom';
import axios from 'axios'

function RecoDropdown() {
  const [click, setClick] = useState(false);
  const [ RecoGenres, setGenresReco] = useState([])

  const handleClick = () => setClick(!click);

  function recoGenres(genres){
    axios.get(`http://localhost:5000/recommendations/${genres}`).then(response => {
    setGenresReco(response.data.tracks); 
  });
}

  return (
    <>
      <ul
        onClick={handleClick}
        className={click ? 'dropdown-menu clicked' : 'dropdown-menu'}
      >
        {RecoMenuItems.map((item, index) => {
          return (
            <li key={index}>
              <button
                className={item.cName}
                onClick={() => recoGenres(item.path)}
              >
                {item.title}
              </button>
            </li>
          );
        })}
      </ul>
    </>
  );
}

export default RecoDropdown;
