import React, { useState, useEffect } from 'react';
import './Artists.css';
import Artisttem from './ArtistItem';
import TrackItem from './TrackItem';
import axios from 'axios'

function Recommendations() {
  const [ArtistReco, setArtists] = useState([])
  const [TracksReco, setTracks] = useState([])
  const [genres, setGenres] = useState([])
  const [Recommendations, setReco] = useState([])
  let count = 1;
  let artistCount = 0

  useEffect(() => {
    axios.get('http://localhost:5000/artists').then(response => {
      setArtists(response.data.items);
    });
  }, [])

  const Artits_reco_id = ArtistReco.map(artist => {
    return artist.id;
  })

  useEffect(() => {
    axios.get('http://localhost:5000/tracks').then(response => {
      setTracks(response.data.items);
    });
  }, [])

  const Tracks_reco_id = TracksReco.map(track => {
    return track.id;
  })


  useEffect(() => {
    axios.get('http://localhost:5000/artists').then(response => {
        let genres_count = {};
        response.data.items.map(artist => (
            artist.genres.map(genre => (
            genres_count[genre] = (genres_count[genre] || 0) + 1
            ))
        ))
        
        let items = Object.keys(genres_count).map(function(key) {
            return [key, genres_count[key]];
        }).sort(function(first, second) {
            //console.log(first)
            return second[1] - first[1];
        });
        

        items.forEach(item => {
            setGenres(oldState => [...oldState, item[0]]);
        });
    });
  }, [])

  useEffect(() => {
    axios.get(`http://localhost:5000/recommendations/${Artits_reco_id}/${(genres.slice(0, 3).join()).replace(/ /g, "%20")}/${Tracks_reco_id[0]}`).then(response => {
      setReco(response.data.tracks);
    });
  }, [])

  console.log(Recommendations)

  
  
  //console.log(`http://localhost:5000/recommendations/${Artits_reco_id[0]}/${(genres.slice(0, 3).join()).replace(/ /g, "%20")}/${Tracks_reco_id[0]}`)

  //console.log((genres.slice(0, 3).join()).replace(/ /g, "%20"))

  return (
    <div className='cards'>
      <h1>Recommended for you</h1>
      <div className='cards__container'>
        <div className='cards__wrapper'>
          <ul className='cards__items'>
            {
              <div>
                {genres.slice(0,3).map((genre) => {
                  return <p>{genre}</p>;
                  // genres are generated in descending order
                  // after generating the recommendeded tracks, map them here with TrackItem
                })}
              </div> 
            }
            {
              <div>
              {ArtistReco.slice(0, 1).map(artist => {
                return <p>{artist.id}</p>;
              })}
              </div>
            }
            {
              <div>
              {TracksReco.slice(0, 1).map(tracks => {
                return <p>{tracks.id}</p>;
              })}
              </div>
            }
          </ul>
        </div>
      </div>
    </div>
  );

}

export default Recommendations;
