import React, {useState, useEffect} from 'react';
import './Artists.css';
import Artisttem from './ArtistItem';
import axios from 'axios'

function Artists() {
  const [artists, setArtists] = useState([])
  const [selected, setSelected] = useState([])
  let count = 1;
  useEffect(() => {
    axios.get('http://localhost:5000/artists').then(response => {
      setArtists(response.data.items);
      setSelected('medium_term')
    });
  }, [])

  function getArtistsByTimeRange(range) {
    axios
        .get("http://localhost:5000/artists/" + range)
        .then(res => {
            setArtists(res.data.items);
            setSelected(range)
          }
        )
  }

  return (
    <div className='cards'>
      <h1>Top Artists</h1>
      <div className='cards__container'>
        <div className='cards__wrapper'>
          <ul className='cards__items'>
          {['short_term', 'medium_term', 'long_term'].map(key => 
                    <li className='cards__item'>
                    <div className='cards__item__link'>
                      <button className={key === selected ? 'btn-selected btn btn--primary btn--large' : 'btn btn--primary btn--large'} onClick={() => getArtistsByTimeRange(key)}>key</button>
                    </div>
                  </li>
          )}
          </ul> 
        </div>
      </div>
      <div className='cards__container'>
        <div className='cards__wrapper'>
          <ul className='cards__items'>
            {
              artists.map(artist => (
              <Artisttem
                src={artist.images[0].url}
                text={artist.name}
                label={count++}
                path={artist.external_urls.spotify}
                />
              ))
            }
          </ul>
        </div>
      </div>
    </div>
  );
  /*return (
    <div className='cards'>
      <h1>Top Artists</h1>
