import React, { useState } from 'react';
import { RecoMenuItems } from './RecoMenuItems';
import './RecoDropdown.css';
import { Link } from 'react-router-dom';
import axios from 'axios'
import recoGenres from './Recommendations'

function RecoDropdown() {
  const [click, setClick] = useState(false);
  const [Recommendations, setReco] = useState([])
  const handleClick = () => setClick(!click);
  count = 1


  function recoGenres(genres){
    axios.get(`http://localhost:5000/recommendations/${genres}`).then(response => {
    setReco(response.data.tracks); 
  });
}


  return (
    <>
      <ul
        onClick={handleClick}
        className={click ? 'dropdown-menu clicked' : 'dropdown-menu'}
      >
        {RecoMenuItems.map((item, index) => {
          return (
        <div>
            <li key={index}>
              <Link
                className={item.cName}
                to = {'/recommendation'+ item.path}
                onClick={() => setClick(false)}
              >
                {item.title}
              </Link>
            </li>
         
        <div className='cards__container'>
        <div className='cards__wrapper'>
        {
          Recommendations.length === 0 ? (
          recoGenres(item.path)
          ):(
          <ul className='cards__items'>
          {
              Recommendations.map(reco => (
              <RecomItem
                src={reco.album.images[0].url}
                text={reco.name}
                path = {reco.external_urls.spotify} 
                label = {count++}
                text1 = {reco.album.artists[0].name}
                />
              ))
            }
          </ul>
          )}
        </div>
      </div>
      </div>  );
        })}
      </ul>
    </>
  );
}

export default RecoDropdown;
