import React, { useState, useEffect } from 'react';
import './Artists.css';
import Artisttem from './ArtistItem';
import TrackItem from './TrackItem';
import axios from 'axios'

function Recommendations() {
  const genres_count = {};
  let count = 1;

  useEffect(() => {
    axios.get('http://localhost:5000/artists').then(response => {
      response.data.items.map(artist => (
          artist.genres.map(genre => (
            genres_count[genre] = (genres_count[genre] || 0) + 1
          ))
      ))
    });

    console.log(genres_count)
  }, [])


  return (
    <div className='cards'>
      <h1>Recommendations</h1>
      <div className='cards__container'>
        <div className='cards__wrapper'>
          <ul className='cards__items'>
            {
              // map TrackItem here after generating the recommendeded tracks
            }
          </ul>
        </div>
      </div>
    </div>
  );

}

export default Recommendations;
