import React from 'react';
import './Artists.css';
import axios from 'axios'
import Artisttem from './ArtistItem';

function Artists() {


  const [artists, setArtists] = useState([])

  function getArtistsByTimeRange(range) {
    axios
        .get("http://localhost:5000/artists/" + range)
        .then(res => {
            setArtists(res.data.items)
          }
        )
  }
  
  useEffect(() => {
      axios
        .get("http://localhost:5000/artists")
        .then(res => {
            setArtists(res.data.items)
          }
        )
  }, [])

  return (
    <div className='cards'>
      <h1>Top Artists</h1>
      <div className='cards__container'>
        <div className='cards__wrapper'>
          <ul className='cards__items'>
          {
            artists.length === 0 ? (
              <p>Fetching your artists!</p>
            ) : (
              artists.map(artist => (
                <ArtistComponent artist={artist} key={artist}/>
              ))
            )
          }
          </ul>
        </div>
      </div>
    </div>
  );
}

export default Artists;
