import React, { useState, useEffect } from 'react';
import './Artists.css';
import RecomItem from './RecomItems'
import axios from 'axios'
import RecoDropdown from './RecoDropdown'
import { Link } from 'react-router-dom'

function Recommendations(genres) {
  const [Recommendations, setReco] = useState([])
  const [dropdown, setDropdown] = useState(false)
  const onMouseEnter = () => {
    if (window.innerWidth < 960) {
      setDropdown(false);
    } else {
      setDropdown(true);
    }
  };

  const onMouseLeave = () => {
    if (window.innerWidth < 960) {
      setDropdown(false);
    } else {
      setDropdown(false);
    }
  };
  let count = 1;

  function recoGenres(genres){
    axios.get(`http://localhost:5000/recommendations/${genres}`).then(response => {
    setReco(response.data.tracks); 
  });
}

  return (
    <div className='cards'>
      <h1>Recommended for you</h1>
      <li 
      className='reco-nav-item'
      onMouseEnter={onMouseEnter}
      onMouseLeave={onMouseLeave}
      >
        <Link 
        to='/recommendations'
        className='reco-nav-links'
        >
          Genres  <i className='fas fa-caret-down'/>
        </Link>
        {dropdown && <RecoDropdown />}</li>
      <div className='cards__container'>
        <div className='cards__wrapper'>
        {
          Recommendations.length === 0 ? (
          recoGenres(genres)
          ):(
          <ul className='cards__items'>
          {
              Recommendations.map(reco => (
              <RecomItem
                src={reco.album.images[0].url}
                text={reco.name}
                path = {reco.external_urls.spotify} 
                label = {count++}
                text1 = {reco.album.artists[0].name}
                />
              ))
            }
          </ul>
          )}
        </div>
      </div> 
    </div> 
  );
}

export default Recommendations;


export const RecoMenuItems = [
  {
    title: 'Pop',
    path: '/'+ Recommendations(pop),
    cName: 'dropdown-link'
  },
  {
    title: 'Rock',
    cName: 'dropdown-link'
  },
  {
    title: 'Country',
    cName: 'dropdown-link'
  }
]
